mapper.select:


SELECT image_url
FROM product_image
WHERE p_id=#{pId}

SELECT p_id, name, created_at, updated_at, content, category_id, user_id, started_at, ended_at, started_price, bid_increment, status, TIMESTAMPDIFF(SECOND,NOW(),ended_at) AS lefttime FROM product
AND category_id = #{category_id}
AND name LIKE CONCAT('%',#{searchQuery},'%')
p_id DESC ended_at ASC started_price ASC started_price DESC
limit #{pageStart}, #{perPageNum}

SELECT count(*) FROM product
AND category_id = #{category_id}
AND name LIKE CONCAT('%',#{searchQuery},'%')
